<template>
  <a
    type="button"
    @click="toggleWalletModal"
    class="w-max mx-auto mt-10 items-center px-5 py-2.5 font-medium tracking-wide shadow-lg text-white capitalize bg-gradient-to-r from-green-300 via-blue-500 to-purple-600 hover:opacity-75 focus:outline-none focus:bg-gray-900 transition duration-300 transform active:scale-95 ease-in-out"
  >
    <p class="text-lg font-base text-white">Connect Wallet</p>
  </a>
  <div
    v-if="walletModal"
    class="flex items-center justify-center fixed left-0 bottom-0 w-full h-full bg-gray-600 bg-opacity-40"
  >
    <div class="bg-light-blue rounded-lg w-auto">
      <div class="flex flex-col items-start p-4">
        <div class="flex items-center w-full mb-6">
          <div class="text-gray-900 font-medium text-md mr-5">Select preferred wallet</div>
          <a type="button" @click="toggleWalletModal">
            <svg
              class="ml-auto fill-current text-gray-700 w-6 h-6"
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 18 18"
            >
              <path
                d="M14.53 4.53l-1.06-1.06L9 7.94 4.53 3.47 3.47 4.53 7.94 9l-4.47 4.47 1.06 1.06L9 10.06l4.47 4.47 1.06-1.06L10.06 9z"
              />
            </svg>
          </a>
        </div>
        <hr />
        <a
          type="button"
          class="py-3 px-5 mb-2 bg-white text-gray-900 rounded-md text-sm border border-gray-200 w-full relative"
          @click="() => onClickWallet(WalletName.Phantom)"
        >
          Phantom
        <img class="absolute right-3 top-2 w-7 h-7 " src="data:image/svg+xml;base64," alt="Red dot" />
        </a>
        <a
          type="button"
          class="py-3 px-5 mb-2 bg-white text-gray-900 rounded-md text-sm border border-gray-200 w-full relative"
          @click="() => onClickWallet(WalletName.Solflare)"
        >
          Solflare
        <img class="absolute right-3 top-2 w-7 h-7 " src="data:image/svg+xml;base64," alt="Red dot" />
        </a>
        <a
          type="button"
          class="py-3 px-5 mb-2 bg-white text-gray-900 rounded-md text-sm border border-gray-200 w-full relative"
          @click="() => onClickWallet(WalletName.Sollet)"
        >
          Sollet 
        <img class="absolute right-3 top-2 w-7 h-7 " src="data:image/svg+xml;base64," alt="Red dot" />
        </a>
        <a
          type="button"
          class="py-3 px-5 mb-2 bg-white text-gray-900 rounded-md text-sm border border-gray-200 w-full relative"
          @click="() => onClickWallet(WalletName.SolflareWeb)"
        >
          Solflare Extension
        <img class="absolute right-3 top-2 w-7 h-7 " src="data:image/svg+xml;base64," alt="Red dot" />
        </a>
        <a
          type="button"
          class="py-3 px-5 mb-2 bg-white text-gray-900 rounded-md text-sm border border-gray-200 w-full relative"
          @click="() => onClickWallet(WalletName.SolletExtension)"
        >
          Sollet Extension
        <img class="absolute right-3 top-2 w-7 h-7 " src="data:image/png;base64," alt="Red dot" />
        </a>
      </div>
    </div>
  </div>
</template>

<script lang="ts">
import { computed, defineComponent, onMounted, ref } from 'vue';
import { WalletName } from '@solana/wallet-adapter-wallets';
import useCluster, { Cluster } from '@/composables/cluster';
import useWallet from '@/composables/wallet';

export default defineComponent({
  setup() {
    // cluster

    const walletModal = ref<boolean>(false);
    const { cluster, setCluster, getClusterURL } = useCluster();
    const chosenCluster = Cluster.Mainnet;

    // wallet
    const { getWalletName, setWallet } = useWallet();
    const toggleWalletModal = () => {
      walletModal.value = !walletModal.value
    }
    const onClickWallet = (walletName: WalletName) => {
      setWallet(walletName, getClusterURL());
      return getWalletName();
    }
    const chosenWallet = computed({
      get() {
        return getWalletName();
      },
      set(newVal: WalletName | null) {
        setWallet(newVal, getClusterURL());
      },
    });

    return {
      toggleWalletModal,
      walletModal,
      onClickWallet,
      Cluster,
      chosenCluster,
      WalletName,
      chosenWallet,
    };
  },
});
</script>

<style scoped></style>
